mapper.select:
select ,s.s_title,u.nickName from dd_audit t left join dd_scontent s on t.s_contentid = s.s_contentid left join sys_user u on t.w_uid = u.id where 1=1
and s_audit_id = #{s_audit_id,jdbcType=BIGINT}
and (s.s_title like CONCAT('%', #{s_title}, '%') or u.nickName like CONCAT('%', #{s_title}, '%'))
order by t.s_r_time desc
limit #{num1},#{num2}

select count(0) from dd_audit t left join dd_scontent s on t.s_contentid = s.s_contentid left join sys_user u on t.w_uid = u.id where 1=1
and t.s_audit_id = #{s_audit_id,jdbcType=BIGINT}
and (s.s_title like CONCAT('%', #{s_title}, '%') or u.nickName like CONCAT('%', #{s_title}, '%'))
